<?phpnamespace Controleur;require_once './Framework/autoload.php';/**  *  la classe ControleurBillet hérite de la classe abstraite Controleur du framework. Il s'agit du contrôleur des actions liées aux billets  *  Elle utilise les services de la classe Requete pour accéder aux parametres de la requete.   *  elle utilise également la methode executerAction de sa superclasse afin d'actualiser les détails sur le billet, et genererVue pour générer la vue associée à l'action  */class ControleurBillet extends \Framework\Controleur{    /* manager de billet */    private $_managerBillet;       /* manager de commentaire */    private $_managerCommentaire;        /**     * le constructeur instancie les classes "modèles" requises    */     public function __construct(\Framework\Application $app)      {     	 parent::__construct($app);         $this->_managerBillet= new \Modele\ManagerBillet();         $this->_managerCommentaire= new \Modele\ManagerCommentaire();     }         /**    * cette méthode est l'action par défaut consistant à afficher la liste des commentaires associés à un billet    * elle utilise les méthodes getter des modéles instanciées pour récupérer les données nécessaires aux vues    */    public function index()    {        $idBillet =$this->requete->getParametre("id") ;        $billet = $this->_managerBillet->getBillet($idBillet);         $commentaires = $this->_managerCommentaire->getListeCommentaires($idBillet);               $this->genererVue(array('billet'=>$billet,'commentaires'=>$commentaires));    }        /**    * cette méthode est l'action "commenter" permettant de générer / ajouter un commentaire sur un billet    */    public function commenter()    {        $idBillet = $this->requete->getParametre("id");        $auteur = $this->requete->getParametre("auteur");        $contenu = $this->requete->getParametre("contenu");            // appelle de la méthode permettant d'ajouter un commentaire */        $this->_managerCommentaire->ajouterCommentaire($idBillet, $auteur, $contenu);            //il s'agit ensuite d'executer l'action par défaut permettant d'afficher la liste des commentaires */        $this->executerAction("index");    }}