<?phpnamespace Modele;require_once './Framework/autoload.php';/*** cette classe reprÃ©sente l'entitÃ© Billet du forum*/class Billet extends \Framework\Entite{  public $date;  public $dateModif;  public  $titre;  public  $contenu;  public $auteur;    const AUTEUR_INVALIDE=1;  const TITRE_INVALIDE=2;  const CONTENU_INVALIDE=3;      /**    * mÃ©thode testant si l'objet Billet est valide    * elle utilise la fonction "empty(var)" qui  retourne FALSE si la variable existe et est non vide    *    * @return Boolean TRUE valide / FALSE non valide    */    public function isValid()    {        return !(empty($this->auteur) || empty($this->contenu) || empty($this->titre));    }        /**    * mÃ©thodes "setters" des attributs privÃ©s    */    public function setTitre($titre)    {        // $titre doit tre une cha”ne de caractre infŽrieure ˆ 30    	if (!is_string($titre) || empty($titre) || strlen($titre) > 30)        {        	$this->erreurs[]=self::TITRE_INVALIDE;        }        else         {        	$this->titre=$titre;        }    }        public function setAuteur($auteur)    {    	// $auteur doit tre une cha”ne de caractre infŽrieure ˆ 15    	if (!is_string($auteur) || empty($auteur) || strlen($auteur) > 15)    	{    		$this->erreurs[]=self::AUTEUR_INVALIDE;    	}    	else    	{    		$this->auteur=$auteur;    	}    }        /**     * @param unknown $contenu     */    public function setContenu($contenu)    {    	if (!is_string($contenu) || empty($contenu))    	{    		$this->erreurs[]=self::CONTENU_INVALIDE;    	}    	else    	{    		$this->contenu=$contenu;    	}    }        /**     * @param \DateTime $date     */    public function setDate(\DateTime $date)    {    	$this->date=$date;     }        public function setDateModif(\DateTime $dateModif)    {    	$this->dateModif=$dateModif;    }    /**     * mÃ©thodes "getters" des attributs privÃ©s     */    public function auteur()    {    	return $this->auteur;	    }        public function titre()    {    	return $this->titre;    }        public function contenu()    {    	return $this->contenu;    }        /**     * @return DateTime     */    public function date()    {    	return $this->date;    }        /**     * @return DateTime     */    public function dateModif()    {    	return $this->dateModif;    }}                   