<?phpnamespace Framework;/***  la classe Requ√™te aura le r√¥le de mod√©liser une requ√™te HTTP entrante*/class Requete extends ApplicationComponent{    /* Tableau des parametres de la requ√™te */    private $_parametres;        /**    * cette m√©thode permet de construire l'objet requ√™te    *    * @param array $parametres Tableau Param√®tres de la requ√™te    */     public function __construct($app,$parametres)     {     	parent::__construct($app);     	$this->_parametres = $parametres;     }         /**    * cette m√©thode teste si le parametre existe bien dans la requ√™te    *    * @param string $nom Nom du param√®tre    * @return Boolean - vrai si le param√®tre existe dans la requ√™te et sa valeur n'est pas vide    */    public function existeParametre($nom)    {        return (isset($this->_parametres[$nom]) && $this->_parametres[$nom] != "");    }         /**    * cette m√©thode renvoie la valeur du parametre demand√© et l√®ve une exception si le parametre est introuvable    *    * @param string $nom Nom du param√®tre    * @return string Valeur du param√®tre    * @throws Exception Si le param√®tre n'existe pas dans la requ√™te    */     public function getParametre($nom)     {         /* v√©rification de l'existence du parametre */         if ($this->existeParametre($nom))        {            return $this->_parametres[$nom];        }        else        {            throw new \Exception ("Parametre '$nom' absent de la requ√™te");        }                 }        /**    * cette methode détermine si un cookie existe    *     * @param string $key nom du cookie    * @return boolean TRUE si la valeur du cookie existe    */    public function cookieExists($key)    {    	return isset ($_COOKIE[$key]);    }        /**    * cette méthode renvoie le Cookie    *     * @param string $key nom du cookie    * @return mixed valeur du cookie    */    public function cookieData($key)    {    	// operateur ternaire     	return isset($_COOKIE[$key]) ? $_COOKIE[$key]:null;    }}     