<!-- Portion de Vue spécifique à l'affichage des commentaires d'un Billet du  Forum --><?php $this->_titre = "Blog AtelierBlancNordOuest- ".$this->nettoyer($billet['titre']);?><table>	<caption>		<h1 class="titreBillet"><?= $this->nettoyer($billet['titre']) ?></h1>	</caption>	<tr>    	<td>       		<h2 id="titreReponses"><?= $this->nettoyer($billet['auteur']) ?></h2>    	</td>    	<td>        	<time><?= $this->nettoyer($billet['date']->format('d/m/Y - H\hi')) ?></time>    	</td>  		<td>    		<p><?= $this->nettoyer($billet['contenu']) ?></p>    	</td>	</tr>       <!-- Affichage des commentaires du Billet -->    <?php foreach ($commentaires as $commentaire): ?>    	<tr>        	<td>        		<h2 id="titreReponses"><?= $this->nettoyer($commentaire['auteur']) ?></h2>  	        	</td>        	<td>        		<time><?= $this->nettoyer($commentaire['date']->format('d/m/Y - H\hi')) ?></time>        	</td>        	<td>        		<p><?= $this->nettoyer($commentaire['contenu']) ?></p>        	</td>    	</tr>    <?php endforeach; ?></table>   <!-- Formulaire de création de commentaire -->    <form method="post" action="billet/commenter">        <input id="auteur" name="auteur" type="text" placeholder="Votre pseudo" required /><br />        <textarea id="txtCommentaire" name="contenu" rows="6" placeholder="Votre commentaire" required></textarea><br />        <input type="hidden" name="id" value="<?= $billet['id'] ?>" />        <input type="submit" value="Commenter" />    </form>